function randomInteger(min, max){
    let rand = min + Math.random() * (max - min);
    return Math.floor(rand);
}

$(document).ready ( function() {
    let n = 4;
    let language = "eng";
    let sizeField = 400;
    createInputField(n, sizeField);
    createHistory();
    let countColumn = 0;
    let countRow = 0;
    let dictWords;
    if (language == "rus"){
        dictWords = importRusWords();
    }
    else {
        dictWords = importEngWords();
    }
    let randomIndex = randomInteger(0, dictWords[n].length);
    let word = dictWords[n][randomIndex];
    console.log(word);
    $('.check').click(function(){
        let inputWord = "";
        let form = document.forms.inputWord;
        for (let i = 0; i < n; i++){
            inputWord += form.elements[i].value;
        }
        inputWord = inputWord.toLowerCase();
        let flag = true;
        if (new Set(inputWord).size != n){
            $('.info').empty();
            $('.info').append("Wrong word");
            flag = false;
        }
        else if (!(dictWords[n].includes(inputWord))){
            $('.info').empty();
            $('.info').append("Word not in dictionary");
        }
        else{
            let cows = 0;
            let bulls = 0;
            for (let i = 0; i < n; i++){
                if (word[i] == inputWord[i]){
                    bulls++;
                }
                else if (word.includes(inputWord[i])){
                    cows++;
                }
            }
            $('.info').empty();
            if (bulls != n){
                $('.info').append("Found " + bulls + " bulls and " + cows + " cows.");
            } else {
                $('.info').append("Hooray! You win!")
            }
            countRow %= 13;
            if (countRow == 0){
                countColumn++;
                createColumn(countColumn);
            }
            let line = document.createElement("tr");
            let wordTd = document.createElement("td");
            wordTd.append(inputWord);
            line.append(wordTd);
            let bullsTd = document.createElement("td");
            bullsTd.append(bulls);
            line.append(bullsTd);
            let cowsTd = document.createElement("td");
            cowsTd.append(cows);
            line.append(cowsTd);
            $('#column' + countColumn).append(line)
            countRow++;
        }
    });
    $('.giveUp').click(function(){
        let losePhrase = document.createElement("h1");
        losePhrase.className = "losePhrase";
        if (language == "rus"){
            losePhrase.append("Вы проиграли! Слово было " + word);
        }
        else{
            losePhrase.append("You lose! Word was " + word);
        }
        $('.inputField').empty();
        $('.inputField').append(losePhrase);
    })
});